            var numOfSlidesToScroll = 2;
            var loopItems = true;
            var pauseOnHover = true;
            var isRtl = false;
            var autoscrollTimeout = 3;
            var autoscroll = autoscrollTimeout > 0 ? true : false;
            var navigationArrows = true;
            var navigationDots = false;
            var numberOfVisibleItems = 7 < 1 ? 1 : 7;
            var animationSpeedString = 'slow';
            var animationSpeed;

            switch(animationSpeedString) {
                case 'slow':
                    animationSpeed = 300;
                    break;
                case 'fast':
                    animationSpeed = 150;
                    break;
                default:
                    animationSpeed = 0;
            }

            var responsiveBreakpointsObj = {};

            try {

                responsiveBreakpointsObj = JSON.parse('[{"breakpoint":890,"settings":{"slidesToShow":3}},{"breakpoint":711,"settings":{"slidesToShow":2}},{"breakpoint":425,"settings":{"slidesToShow":1}}]');

                for (var i = 0; i < responsiveBreakpointsObj.length; i++) {
                    if (responsiveBreakpointsObj[i].settings.slidesToShow < numOfSlidesToScroll) {
                        responsiveBreakpointsObj[i].settings.slidesToScroll = responsiveBreakpointsObj[i].settings.slidesToShow;
                    }
                }
            }
            catch (e) {
                console.log('Invalid slick slider responsive breakpoints setting value!');
            }

            jCarousel.on('init', function(slick) {
                $.event.trigger({ type: "newProductsAddedToPageEvent" });
                $(".carousel-title").show();
            });

            jCarousel.slick({
                rtl: isRtl,
                infinite: loopItems,
                slidesToShow: numberOfVisibleItems,
                slidesToScroll: numOfSlidesToScroll,
                dots: navigationDots,
                speed: animationSpeed,
                autoplay: autoscroll,
                autoplaySpeed: autoscrollTimeout * 1000,
                arrows: navigationArrows,
                appendArrows: '#jcarousel-191-287 .carousel-title',
                cssEase: 'linear',
                respondTo: 'slider',
                edgeFriction: 0.05,
                initialSlide: 1 - 1,
                pauseOnHover: pauseOnHover,
                draggable: false,
                responsive: responsiveBreakpointsObj
            });
        });
