<script>
    $(function () {
        $('[class*="view-all-link-"]').each(function () {
            var titleText = $(this).find('.carousel-title > span').text();
            var viewAllLink = Array.from(this.classList).find(cls => cls.indexOf('view-all-link-') > -1).replace('view-all-link-', '');
            $(this).find('.carousel-title > span').html('<a href="' + viewAllLink + '">' + titleText + '</a>');

            $(this).find('.carousel-title > span').hover(function () {
                $(this).addClass('hover-color');
            }, function () {
                $(this).removeClass('hover-color');
            })

            //$('<a style="text-transform: none; margin-left: 10px;" href="' + viewAllLink + '">View All</a>').appendTo($(this).find('.carousel-title > span'));
        });

        $('.page.home-page').remove();

        // This will detect and set the height properly when the window resizes
        var callback = function () {
            var t = $('#myModal'),
                d = t.find('.modal-content'),
                dh = d.height(),
                w = $(window).width(),
                h = window.innerHeight;
            // if it is desktop & dialog is lower than viewport
            // (set your own values)
            if (w > 380 && (dh + 60) < h) {
                d.css('margin-top', Math.round(0.96 * (h - dh) / 2));
            } else {
                d.css('margin-top', '');
            }
        }

        // Binding the callback in document.ready is not required, just on window.resize
        $(window).resize(callback);

        $('#myModal').on('shown.bs.modal', callback);

        // popup notification advertising
        OpenAdvertisingModal(this, '/Home/PopupNotifications');
    });
</script>





